fs.readFile("santa.txt", (err, data) => {
    console.time("time");
 
    directions = data.toString();
 
    const directionsArray = directions.split("");
    let sum = 0;
    directionsArray.map(sym => {
        if (sym === "(") {
            return sum++;
        }
        if (sym === ")") {
            return sum--;
        }
    });
    console.timeEnd("time");
    console.log(sum);
});
